<style>
	*,
	*:before,
	*:after {
		box-sizing: border-box;
	}
	main {
		background-color: #6c78a9;
		display: flex;
		align-items: center;
		justify-content: center;
	}
	.rain__drop {
		animation-delay: calc(var(--d) * 1s);
		animation-duration: calc(var(--a) * 1s);
		animation-iteration-count: infinite;
		animation-name: drop;
		animation-timing-function: linear;
		height: 30px;
		left: calc(var(--x) * 1%);
		position: absolute;
		top: calc((var(--y) + 50) * -1px);
	}
	.rain__drop path {
		fill: #a1c6cc;
		opacity: var(--o);
		transform: scaleY(calc(var(--s) * 1.5));
	}
	@-moz-keyframes drop {
		90% {
			opacity: 1;
		}
		100% {
			opacity: 0;
			transform: translateY(100vh);
		}
	}
	@-webkit-keyframes drop {
		90% {
			opacity: 1;
		}
		100% {
			opacity: 0;
			transform: translateY(100vh);
		}
	}
	@-o-keyframes drop {
		90% {
			opacity: 1;
		}
		100% {
			opacity: 0;
			transform: translateY(100vh);
		}
	}
	@keyframes drop {
		90% {
			opacity: 1;
		}
		100% {
			opacity: 0;
			transform: translateY(100vh);
		}
	}
</style>

